import jwt from 'jsonwebtoken';
import bcrypt from 'bcrypt';
import rateLimit from 'express-rate-limit';
import { Request, Response, NextFunction } from 'express';

// JWT configuration
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key-change-in-production';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '1h';
const JWT_REFRESH_EXPIRES_IN = process.env.JWT_REFRESH_EXPIRES_IN || '7d';

// Password hashing utilities
export class PasswordUtils {
  static async hash(password: string): Promise<string> {
    const saltRounds = 12; // As specified in prompt
    return bcrypt.hash(password, saltRounds);
  }

  static async verify(password: string, hash: string): Promise<boolean> {
    return bcrypt.compare(password, hash);
  }
}

// JWT utilities
export class JWTUtils {
  static sign(payload: object, expiresIn: string = JWT_EXPIRES_IN): string {
    return jwt.sign(payload, JWT_SECRET, { 
      expiresIn: expiresIn as any,
      algorithm: 'HS256' // Using HS256 for simplicity, RS256 for production
    });
  }

  static verify(token: string): any {
    return jwt.verify(token, JWT_SECRET);
  }

  static generateTokens(user: { id: string; email: string; role: string }) {
    const accessToken = this.sign({ 
      userId: user.id, 
      email: user.email, 
      role: user.role 
    }, JWT_EXPIRES_IN);

    const refreshToken = this.sign({ 
      userId: user.id 
    }, JWT_REFRESH_EXPIRES_IN);

    return { accessToken, refreshToken };
  }
}

// Authentication middleware
export const authenticateJWT = (req: Request, res: Response, next: NextFunction) => {
  const authHeader = req.headers.authorization;
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) {
    return res.status(401).json({ error: 'Access token required' });
  }

  try {
    const decoded = JWTUtils.verify(token);
    (req as any).user = decoded;
    next();
  } catch (error) {
    return res.status(403).json({ error: 'Invalid or expired token' });
  }
};

// Role-based authorization middleware
export const requireRole = (roles: string[]) => {
  return (req: Request, res: Response, next: NextFunction) => {
    const user = (req as any).user;

    if (!user || !roles.includes(user.role)) {
      return res.status(403).json({ error: 'Insufficient permissions' });
    }

    next();
  };
};

// Rate limiting middleware - 100 req/min per IP as specified
export const rateLimiter = rateLimit({
  windowMs: 60 * 1000, // 1 minute
  max: 100, // 100 requests per windowMs
  message: {
    error: 'Too many requests from this IP, please try again later.'
  },
  standardHeaders: true,
  legacyHeaders: false,
  // Remove custom keyGenerator to use default IPv6-safe implementation
});

// Quiz-specific rate limiting - 50 req/min to prevent AI abuse
export const quizRateLimiter = rateLimit({
  windowMs: 60 * 1000, // 1 minute
  max: 50, // 50 requests per minute for quiz endpoints
  message: {
    error: 'Too many quiz requests, please try again later.'
  },
  // For authenticated users, rate limit by user ID, otherwise use default IP-based limiting
  skip: (req) => {
    // Skip rate limiting for authenticated users (they get user-based limiting instead)
    const user = (req as any).user;
    return user && user.userId;
  }
});

// Input validation middleware
export const validateInput = (schema: any) => {
  return (req: Request, res: Response, next: NextFunction) => {
    const { error } = schema.validate(req.body);

    if (error) {
      return res.status(400).json({
        error: 'Validation failed',
        details: error.details.map((detail: any) => detail.message)
      });
    }

    next();
  };
};

