import { Router } from 'express';
import { z } from 'zod';
import { storage } from '../../storage';
import { authenticateJWT, requireRole } from '../../middleware/auth';

const router = Router();

// Validation schemas
const createOrderSchema = z.object({
  items: z.array(z.object({
    productId: z.string(),
    quantity: z.number().min(1, 'Quantity must be at least 1')
  })).min(1, 'At least one item is required'),
  shippingAddress: z.object({
    street: z.string().min(1, 'Street address is required'),
    city: z.string().min(1, 'City is required'),
    zip: z.string().min(1, 'ZIP code is required'),
    country: z.string().min(1, 'Country is required')
  }),
  paymentMethod: z.enum(['stripe', 'paypal', 'apple_pay']).default('stripe'),
  shippingType: z.enum(['standard', 'express', 'overnight']).default('standard')
});

const updateOrderStatusSchema = z.object({
  status: z.enum(['pending', 'processing', 'shipped', 'delivered', 'cancelled'])
});

// Calculate shipping costs based on type
function calculateShipping(shippingType: string, subtotal: number): number {
  const freeShippingThreshold = 50;

  if (subtotal >= freeShippingThreshold) {
    return 0; // Free shipping over $50
  }

  switch (shippingType) {
    case 'express': return 15.99;
    case 'overnight': return 25.99;
    case 'standard':
    default: return 5.99;
  }
}

// Calculate carbon offset and trees planted
function calculateSustainabilityMetrics(items: any[], products: any[]) {
  let totalCo2 = 0;
  let treesPlanted = 0;

  items.forEach(item => {
    const product = products.find(p => p.id === item.productId);
    if (product) {
      const productCo2 = parseFloat(product.carbonFootprint || '0');
      totalCo2 += productCo2 * item.quantity;

      // Plant 1 tree for every $25 spent on sustainable products
      if (product.sustainabilityScore && product.sustainabilityScore > 70) {
        const itemTotal = parseFloat(product.price) * item.quantity;
        treesPlanted += Math.floor(itemTotal / 25);
      }
    }
  });

  return { totalCo2, treesPlanted };
}

// POST /api/v1/orders - Create new order
router.post('/', authenticateJWT, async (req, res) => {
  try {
    const orderData = createOrderSchema.parse(req.body);
    const userId = (req as any).user.userId;

    // Validate products and calculate totals
    const products = await Promise.all(
      orderData.items.map(item => storage.getProduct(item.productId))
    );

    // Check if all products exist and have sufficient stock
    for (let i = 0; i < products.length; i++) {
      const product = products[i];
      const item = orderData.items[i];

      if (!product) {
        return res.status(400).json({ 
          error: `Product not found: ${item.productId}` 
        });
      }

      if (product.stock < item.quantity) {
        return res.status(400).json({ 
          error: `Insufficient stock for ${product.name}. Available: ${product.stock}, Requested: ${item.quantity}` 
        });
      }
    }

    // Calculate totals
    let subtotal = 0;
    orderData.items.forEach((item, index) => {
      const product = products[index]!;
      subtotal += parseFloat(product.price) * item.quantity;
    });

    const shipping = calculateShipping(orderData.shippingType, subtotal);
    const total = subtotal + shipping;

    // Calculate sustainability metrics
    const { totalCo2, treesPlanted } = calculateSustainabilityMetrics(orderData.items, products);

    // Create order
    const order = await storage.createOrder({
      userId,
      subtotal: subtotal.toString(),
      shipping: shipping.toString(),
      total: total.toString(),
      shippingAddress: orderData.shippingAddress,
      paymentMethod: orderData.paymentMethod,
      paymentStatus: 'pending',
      shippingType: orderData.shippingType,
      carbonOffset: totalCo2.toString(),
      treesPlanted,
      status: 'pending'
    });

    // TODO: Integrate with Stripe for payment processing
    // For now, we'll simulate successful payment

    // Update product stock (this should be in a transaction in production)
    for (let i = 0; i < products.length; i++) {
      const product = products[i]!;
      const item = orderData.items[i];

      await storage.updateProduct(product.id, {
        stock: product.stock - item.quantity
      });
    }

    // Update user's sustainability metrics
    const user = await storage.getUser(userId);
    if (user) {
      await storage.updateUser(userId, {
        treesPlanted: (user.treesPlanted || 0) + treesPlanted,
        co2Offset: (parseFloat(user.co2Offset || '0') + totalCo2).toString()
      });
    }

    res.status(201).json({
      order,
      items: orderData.items.map((item, index) => ({
        ...item,
        product: products[index],
        priceAtPurchase: products[index]!.price
      })),
      sustainabilityImpact: {
        co2Offset: totalCo2,
        treesPlanted,
        message: treesPlanted > 0 ? 
          `Thanks to your purchase, we'll plant ${treesPlanted} tree${treesPlanted > 1 ? 's' : ''} and offset ${totalCo2.toFixed(2)}kg of CO2!` :
          `Your order will offset ${totalCo2.toFixed(2)}kg of CO2.`
      }
    });

  } catch (error) {
    console.error('Order creation error:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Validation failed', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to create order' });
  }
});

// GET /api/v1/orders - Get user's orders
router.get('/', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const orders = await storage.getOrdersByUser(userId);

    res.json({ orders });
  } catch (error) {
    console.error('Error fetching orders:', error);
    res.status(500).json({ error: 'Failed to fetch orders' });
  }
});

// GET /api/v1/orders/:id - Get specific order
router.get('/:id', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const userRole = (req as any).user.role;
    const orderId = req.params.id;

    const order = await storage.getOrder(orderId);

    if (!order) {
      return res.status(404).json({ error: 'Order not found' });
    }

    // Users can only see their own orders, admins can see all
    if (order.userId !== userId && userRole !== 'admin') {
      return res.status(403).json({ error: 'Access denied' });
    }

    res.json({ order });
  } catch (error) {
    console.error('Error fetching order:', error);
    res.status(500).json({ error: 'Failed to fetch order' });
  }
});

// PUT /api/v1/orders/:id/status - Update order status (admin only)
router.put('/:id/status', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const { status } = updateOrderStatusSchema.parse(req.body);
    const orderId = req.params.id;

    const order = await storage.getOrder(orderId);

    if (!order) {
      return res.status(404).json({ error: 'Order not found' });
    }

    // Update order status
    const updatedOrder = await storage.updateOrder(orderId, { status });

    // TODO: Send notification to user about status change
    // TODO: Trigger webhook to supplier API for dropshipping fulfillment

    res.json({ 
      order: updatedOrder,
      message: `Order status updated to ${status}` 
    });

  } catch (error) {
    console.error('Error updating order status:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Validation failed', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to update order status' });
  }
});

// GET /api/v1/orders/admin/all - Get all orders (admin only)
router.get('/admin/all', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const orders = await storage.getOrders();

    res.json({ 
      orders,
      total: orders.length 
    });
  } catch (error) {
    console.error('Error fetching all orders:', error);
    res.status(500).json({ error: 'Failed to fetch orders' });
  }
});

export default router;

