import { Router } from 'express';
import { storage } from '../../storage';
import { authenticateJWT, requireRole } from '../../middleware/auth';
import { z } from 'zod';

const router = Router();

// Validation schemas
const productQuerySchema = z.object({
  category: z.string().optional(),
  price_min: z.string().transform(Number).optional(),
  price_max: z.string().transform(Number).optional(),
  search: z.string().optional(),
  limit: z.string().transform(Number).default(20),
  offset: z.string().transform(Number).default(0),
  sort: z.enum(['price_asc', 'price_desc', 'rating_desc', 'created_desc']).default('created_desc')
});

const createProductSchema = z.object({
  name: z.string().min(5, 'Name must be at least 5 characters'),
  description: z.string().min(10, 'Description must be at least 10 characters'),
  price: z.number().positive('Price must be positive'),
  cost: z.number().min(0, 'Cost must be non-negative').optional(),
  sku: z.string().min(3, 'SKU must be at least 3 characters'),
  category: z.enum(['serums', 'makeup', 'kits', 'supplements']),
  subcategory: z.string().optional(),
  ingredients: z.array(z.object({
    name: z.string(),
    percentage: z.number().optional(),
    source: z.string().optional()
  })),
  sustainabilityMetrics: z.object({
    co2PerUnit: z.number().optional(),
    recycledPackaging: z.boolean().optional(),
    offsetProgram: z.string().optional()
  }).optional(),
  stockQuantity: z.number().min(0, 'Stock must be non-negative').default(0),
  imageUrls: z.array(z.string().url()).optional(),
  isVegan: z.boolean().default(false),
  isCrueltyFree: z.boolean().default(false),
  isOrganic: z.boolean().default(false)
});

// GET /api/v1/products - List products with filtering and pagination
router.get('/', async (req, res) => {
  try {
    const query = productQuerySchema.parse(req.query);

    let products = await storage.getProducts();

    // Apply filters
    if (query.category) {
      products = products.filter(p => p.category === query.category);
    }

    if (query.price_min !== undefined) {
      products = products.filter(p => parseFloat(p.price) >= query.price_min!);
    }

    if (query.price_max !== undefined) {
      products = products.filter(p => parseFloat(p.price) <= query.price_max!);
    }

    if (query.search) {
      const searchTerm = query.search.toLowerCase();
      products = products.filter(p => 
        p.name.toLowerCase().includes(searchTerm) ||
        p.description?.toLowerCase().includes(searchTerm) ||
        p.category.toLowerCase().includes(searchTerm)
      );
    }

    // Apply sorting
    products.sort((a, b) => {
      switch (query.sort) {
        case 'price_asc':
          return parseFloat(a.price) - parseFloat(b.price);
        case 'price_desc':
          return parseFloat(b.price) - parseFloat(a.price);
        case 'rating_desc':
          return parseFloat(b.rating || '0') - parseFloat(a.rating || '0');
        case 'created_desc':
        default:
          return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
      }
    });

    // Apply pagination
    const total = products.length;
    const paginatedProducts = products.slice(query.offset, query.offset + query.limit);
    const page = Math.floor(query.offset / query.limit) + 1;

    res.json({
      data: paginatedProducts,
      pagination: {
        total,
        page,
        limit: query.limit,
        offset: query.offset,
        totalPages: Math.ceil(total / query.limit)
      }
    });
  } catch (error) {
    console.error('Error fetching products:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Invalid query parameters', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to fetch products' });
  }
});

// GET /api/v1/products/:id - Get single product with related data
router.get('/:id', async (req, res) => {
  try {
    const product = await storage.getProduct(req.params.id);

    if (!product) {
      return res.status(404).json({ error: 'Product not found' });
    }

    // TODO: Add related products logic based on category
    const relatedProducts = await storage.getProductsByCategory(product.category);
    const related = relatedProducts
      .filter(p => p.id !== product.id)
      .slice(0, 4);

    res.json({
      ...product,
      relatedProducts: related
    });
  } catch (error) {
    console.error('Error fetching product:', error);
    res.status(500).json({ error: 'Failed to fetch product' });
  }
});

// POST /api/v1/products - Create new product (admin only)
router.post('/', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const productData = createProductSchema.parse(req.body);

    // Generate slug from name
    const slug = productData.name
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/(^-|-$)/g, '');

    const product = await storage.createProduct({
      ...productData,
      price: productData.price.toString(),
      cost: productData.cost?.toString() || '0',
      stock: productData.stockQuantity,
      images: productData.imageUrls || [],
      carbonFootprint: productData.sustainabilityMetrics?.co2PerUnit?.toString() || '0'
    });

    res.status(201).json(product);
  } catch (error) {
    console.error('Error creating product:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Validation failed', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to create product' });
  }
});

// PUT /api/v1/products/:id - Update product (admin only)
router.put('/:id', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const productData = createProductSchema.partial().parse(req.body);

    const updates: any = {};
    if (productData.name) updates.name = productData.name;
    if (productData.description) updates.description = productData.description;
    if (productData.price) updates.price = productData.price.toString();
    if (productData.cost !== undefined) updates.cost = productData.cost.toString();
    if (productData.stockQuantity !== undefined) updates.stock = productData.stockQuantity;
    if (productData.imageUrls) updates.images = productData.imageUrls;

    const product = await storage.updateProduct(req.params.id, updates);

    if (!product) {
      return res.status(404).json({ error: 'Product not found' });
    }

    res.json(product);
  } catch (error) {
    console.error('Error updating product:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Validation failed', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to update product' });
  }
});

// DELETE /api/v1/products/:id - Delete product (admin only)
router.delete('/:id', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const success = await storage.deleteProduct(req.params.id);

    if (!success) {
      return res.status(404).json({ error: 'Product not found' });
    }

    res.status(204).send();
  } catch (error) {
    console.error('Error deleting product:', error);
    res.status(500).json({ error: 'Failed to delete product' });
  }
});

export default router;

