import { Router } from 'express';
import { z } from 'zod';
import { storage } from '../../storage';
import { authenticateJWT, quizRateLimiter } from '../../middleware/auth';

const router = Router();

// Validation schema for quiz submission
const quizAnswersSchema = z.object({
  skinType: z.enum(['oily', 'dry', 'combination', 'sensitive']),
  concerns: z.array(z.string()).min(1, 'At least one concern is required'),
  ageRange: z.enum(['18-25', '26-35', '36-45', '46-55', '55+']).optional(),
  budget: z.enum(['under-25', '25-50', '50-100', 'over-100']).optional(),
  veganOnly: z.boolean().default(false),
  sustainabilityImportant: z.boolean().default(false),
  currentRoutine: z.array(z.string()).default([]),
  allergies: z.array(z.string()).default([])
});

// AI-powered product recommendation logic
async function generateRecommendations(answers: z.infer<typeof quizAnswersSchema>) {
  try {
    // Get all products
    const allProducts = await storage.getProducts();

    // Filter products based on user preferences
    let recommendedProducts = allProducts.filter(product => {
      // Filter by vegan preference
      if (answers.veganOnly && !product.isVegan) {
        return false;
      }

      // Filter by skin type
      if (product.skinTypes && product.skinTypes.length > 0) {
        if (!product.skinTypes.includes(answers.skinType)) {
          return false;
        }
      }

      // Filter by concerns
      if (product.concerns && product.concerns.length > 0) {
        const hasMatchingConcern = product.concerns.some(concern => 
          answers.concerns.includes(concern)
        );
        if (!hasMatchingConcern) {
          return false;
        }
      }

      // Filter by budget if specified
      if (answers.budget) {
        const price = parseFloat(product.price);
        switch (answers.budget) {
          case 'under-25':
            if (price >= 25) return false;
            break;
          case '25-50':
            if (price < 25 || price >= 50) return false;
            break;
          case '50-100':
            if (price < 50 || price >= 100) return false;
            break;
          case 'over-100':
            if (price < 100) return false;
            break;
        }
      }

      return true;
    });

    // Score products based on relevance
    recommendedProducts = recommendedProducts.map(product => {
      let score = 0;

      // Higher score for featured products
      if (product.featured) score += 10;

      // Higher score for trending products
      if (product.trending) score += 5;

      // Higher score for higher ratings
      if (product.rating) {
        score += parseFloat(product.rating) * 2;
      }

      // Higher score for sustainability if important to user
      if (answers.sustainabilityImportant && product.sustainabilityScore) {
        score += product.sustainabilityScore / 10;
      }

      // Bonus for bakuchiol products if anti-aging concern
      if (answers.concerns.includes('aging') && 
          product.ingredients?.some(ing => ing.name.toLowerCase().includes('bakuchiol'))) {
        score += 15;
      }

      return { ...product, recommendationScore: score };
    });

    // Sort by score and return top 5
    recommendedProducts.sort((a, b) => b.recommendationScore - a.recommendationScore);

    return recommendedProducts.slice(0, 5).map(product => ({
      id: product.id,
      name: product.name,
      price: product.price,
      rating: product.rating,
      images: product.images,
      sustainabilityScore: product.sustainabilityScore,
      whyRecommended: generateRecommendationReason(product, answers)
    }));

  } catch (error) {
    console.error('Error generating recommendations:', error);
    throw error;
  }
}

function generateRecommendationReason(product: any, answers: z.infer<typeof quizAnswersSchema>): string {
  const reasons = [];

  if (product.skinTypes?.includes(answers.skinType)) {
    reasons.push(`perfect for ${answers.skinType} skin`);
  }

  const matchingConcerns = product.concerns?.filter((concern: string) => 
    answers.concerns.includes(concern)
  );
  if (matchingConcerns && matchingConcerns.length > 0) {
    reasons.push(`addresses your ${matchingConcerns.join(' and ')} concerns`);
  }

  if (answers.veganOnly && product.isVegan) {
    reasons.push('100% vegan formula');
  }

  if (answers.sustainabilityImportant && product.sustainabilityScore && product.sustainabilityScore > 80) {
    reasons.push('eco-friendly with high sustainability score');
  }

  if (product.ingredients?.some((ing: any) => ing.name.toLowerCase().includes('bakuchiol')) && 
      answers.concerns.includes('aging')) {
    reasons.push('contains bakuchiol, a gentle retinol alternative');
  }

  return reasons.length > 0 ? 
    `Recommended because it's ${reasons.join(', ')}.` : 
    'Recommended based on your preferences.';
}

// POST /api/v1/quiz - Submit quiz and get recommendations
router.post('/', quizRateLimiter, authenticateJWT, async (req, res) => {
  try {
    const answers = quizAnswersSchema.parse(req.body);
    const userId = (req as any).user.userId;

    // Generate AI-powered recommendations
    const recommendations = await generateRecommendations(answers);

    // Store quiz response
    const quizResponse = await storage.createQuizResponse({
      userId,
      responses: answers,
      recommendations: recommendations.map(r => r.id)
    });

    // Update user preferences
    await storage.updateUser(userId, {
      skinType: answers.skinType,
      skinConcerns: answers.concerns,
      preferences: {
        budget: answers.budget,
        veganOnly: answers.veganOnly,
        sustainabilityImportant: answers.sustainabilityImportant,
        allergies: answers.allergies
      }
    });

    res.json({
      quizId: quizResponse.id,
      recommendations,
      message: 'Your personalized skincare routine is ready!'
    });

  } catch (error) {
    console.error('Quiz processing error:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Invalid quiz answers', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to process quiz' });
  }
});

// GET /api/v1/quiz/recommendations/:userId - Get user recommendations
router.get('/recommendations/:userId', authenticateJWT, async (req, res) => {
  try {
    const requestingUserId = (req as any).user.userId;
    const targetUserId = req.params.userId;

    // Users can only access their own recommendations unless admin
    if (requestingUserId !== targetUserId && (req as any).user.role !== 'admin') {
      return res.status(403).json({ error: 'Access denied' });
    }

    const quizResponse = await storage.getQuizResponse(targetUserId);

    if (!quizResponse || !quizResponse.recommendations) {
      return res.status(404).json({ error: 'No quiz results found. Please take the quiz first.' });
    }

    // Get recommended products
    const products = await Promise.all(
      quizResponse.recommendations.map(async (productId: string) => {
        return await storage.getProduct(productId);
      })
    );

    const validProducts = products.filter(p => p !== undefined);

    res.json({
      quizDate: quizResponse.createdAt,
      recommendations: validProducts,
      message: 'Based on your quiz results, here are your personalized recommendations'
    });

  } catch (error) {
    console.error('Error fetching recommendations:', error);
    res.status(500).json({ error: 'Failed to fetch recommendations' });
  }
});

// GET /api/v1/quiz/retake - Check if user can retake quiz
router.get('/retake', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const lastQuiz = await storage.getQuizResponse(userId);

    if (!lastQuiz) {
      return res.json({ canRetake: true, message: 'You haven\'t taken the quiz yet.' });
    }

    // Allow retaking after 30 days
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const canRetake = new Date(lastQuiz.createdAt) < thirtyDaysAgo;

    res.json({
      canRetake,
      lastQuizDate: lastQuiz.createdAt,
      message: canRetake ? 
        'You can retake the quiz for updated recommendations.' : 
        'You can retake the quiz in 30 days for updated recommendations.'
    });

  } catch (error) {
    console.error('Error checking retake status:', error);
    res.status(500).json({ error: 'Failed to check retake status' });
  }
});

export default router;

