import { Router } from 'express';
import { z } from 'zod';
import { storage } from '../../storage';
import { authenticateJWT, requireRole } from '../../middleware/auth';

const router = Router();

// Validation schemas
const updateUserSchema = z.object({
  firstName: z.string().min(1).optional(),
  lastName: z.string().min(1).optional(),
  skinType: z.enum(['oily', 'dry', 'combination', 'sensitive']).optional(),
  skinConcerns: z.array(z.string()).optional(),
  sustainabilityPreference: z.string().optional(),
  budget: z.string().optional(),
  address: z.object({
    street: z.string().optional(),
    city: z.string().optional(),
    zip: z.string().optional(),
    country: z.string().optional()
  }).optional(),
  consentFlags: z.object({
    gdprConsent: z.boolean().optional(),
    marketingConsent: z.boolean().optional()
  }).optional()
});

// GET /api/v1/users/profile - Get current user profile
router.get('/profile', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const user = await storage.getUser(userId);

    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Don't return sensitive data
    const { passwordHash, ...safeUser } = user;

    res.json({ user: safeUser });
  } catch (error) {
    console.error('Error fetching user profile:', error);
    res.status(500).json({ error: 'Failed to fetch profile' });
  }
});

// PUT /api/v1/users/profile - Update user profile
router.put('/profile', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const updates = updateUserSchema.parse(req.body);

    const updatedUser = await storage.updateUser(userId, updates);

    if (!updatedUser) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Don't return sensitive data
    const { passwordHash, ...safeUser } = updatedUser;

    res.json({ 
      user: safeUser,
      message: 'Profile updated successfully' 
    });
  } catch (error) {
    console.error('Error updating user profile:', error);
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: 'Validation failed', details: error.errors });
    }
    res.status(500).json({ error: 'Failed to update profile' });
  }
});

// GET /api/v1/users/data - Export user data (GDPR compliance)
router.get('/data', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;

    // Get all user-related data
    const user = await storage.getUser(userId);
    const orders = await storage.getOrdersByUser(userId);
    const cartItems = await storage.getCartItems(userId);
    const wishlistItems = await storage.getWishlistItems(userId);
    const quizResponse = await storage.getQuizResponse(userId);

    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    // Remove sensitive data
    const { passwordHash, ...userData } = user;

    const exportData = {
      user: userData,
      orders,
      cartItems,
      wishlistItems,
      quizResponse,
      exportDate: new Date().toISOString(),
      note: 'This is your complete data export as per GDPR requirements.'
    };

    res.json(exportData);
  } catch (error) {
    console.error('Error exporting user data:', error);
    res.status(500).json({ error: 'Failed to export data' });
  }
});

// DELETE /api/v1/users/account - Delete user account (GDPR compliance)
router.delete('/account', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;

    // This will trigger GDPR-compliant deletion in the storage layer
    const success = await storage.deleteUser(userId);

    if (!success) {
      return res.status(404).json({ error: 'User not found' });
    }

    res.json({ 
      message: 'Account deleted successfully. All personal data has been removed in compliance with GDPR.' 
    });
  } catch (error) {
    console.error('Error deleting user account:', error);
    res.status(500).json({ error: 'Failed to delete account' });
  }
});

// GET /api/v1/users/sustainability - Get user sustainability stats
router.get('/sustainability', authenticateJWT, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const user = await storage.getUser(userId);

    if (!user) {
      return res.status(404).json({ error: 'User not found' });
    }

    res.json({
      treesPlanted: user.treesPlanted || 0,
      co2Offset: user.co2Offset || '0',
      loyaltyPoints: user.loyaltyPoints || 0,
      sustainabilityLevel: getSustainabilityLevel(user.treesPlanted || 0),
      message: generateSustainabilityMessage(user.treesPlanted || 0, parseFloat(user.co2Offset || '0'))
    });
  } catch (error) {
    console.error('Error fetching sustainability stats:', error);
    res.status(500).json({ error: 'Failed to fetch sustainability stats' });
  }
});

// GET /api/v1/users/admin/all - Get all users (admin only)
router.get('/admin/all', authenticateJWT, requireRole(['admin']), async (req, res) => {
  try {
    const users = await storage.getUsers();

    // Remove sensitive data
    const safeUsers = users.map(user => {
      const { passwordHash, ...safeUser } = user;
      return safeUser;
    });

    res.json({ 
      users: safeUsers,
      total: safeUsers.length 
    });
  } catch (error) {
    console.error('Error fetching all users:', error);
    res.status(500).json({ error: 'Failed to fetch users' });
  }
});

// Helper functions
function getSustainabilityLevel(treesPlanted: number): string {
  if (treesPlanted >= 50) return 'Eco Champion';
  if (treesPlanted >= 20) return 'Green Warrior';
  if (treesPlanted >= 10) return 'Nature Lover';
  if (treesPlanted >= 5) return 'Eco Friend';
  if (treesPlanted >= 1) return 'Sustainability Starter';
  return 'New to Eco';
}

function generateSustainabilityMessage(treesPlanted: number, co2Offset: number): string {
  if (treesPlanted === 0 && co2Offset === 0) {
    return 'Start your sustainability journey with your first eco-friendly purchase!';
  }

  if (treesPlanted > 0 && co2Offset > 0) {
    return `Amazing! You've helped plant ${treesPlanted} tree${treesPlanted > 1 ? 's' : ''} and offset ${co2Offset.toFixed(2)}kg of CO2. Keep up the great work!`;
  }

  if (treesPlanted > 0) {
    return `You've contributed to planting ${treesPlanted} tree${treesPlanted > 1 ? 's' : ''}! Every tree makes a difference.`;
  }

  return `Your purchases have offset ${co2Offset.toFixed(2)}kg of CO2. Thank you for caring about our planet!`;
}

export default router;

