@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222.2, 84%, 4.9%);
  --muted: hsl(210, 40%, 98%);
  --muted-foreground: hsl(215.4, 16.3%, 46.9%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222.2, 84%, 4.9%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222.2, 84%, 4.9%);
  --border: hsl(214.3, 31.8%, 91.4%);
  --input: hsl(214.3, 31.8%, 91.4%);
  --primary: hsl(120, 61%, 34%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(210, 40%, 98%);
  --secondary-foreground: hsl(222.2, 84%, 4.9%);
  --accent: hsl(152, 24%, 66%);
  --accent-foreground: hsl(222.2, 84%, 4.9%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(120, 61%, 34%);
  --radius: 0.5rem;

  /* EcoGlow custom colors */
  --forest-green: hsl(120, 61%, 34%);
  --light-green: hsl(120, 73%, 75%);
  --dark-green: hsl(120, 100%, 20%);
  --gold-light: hsl(48, 77%, 76%);
  --gold-dark: hsl(48, 85%, 47%);
  --sage-green: hsl(152, 24%, 66%);
  --berry-red: hsl(343, 55%, 38%);
  --cream-beige: hsl(33, 43%, 87%);
}

.dark {
  --background: hsl(222.2, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(217.2, 32.6%, 17.5%);
  --muted-foreground: hsl(215, 20.2%, 65.1%);
  --popover: hsl(222.2, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --card: hsl(222.2, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --border: hsl(217.2, 32.6%, 17.5%);
  --input: hsl(217.2, 32.6%, 17.5%);
  --primary: hsl(120, 100%, 20%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(217.2, 32.6%, 17.5%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(152, 24%, 40%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(120, 100%, 20%);

  /* EcoGlow dark mode colors */
  --forest-green: hsl(120, 100%, 20%);
  --light-green: hsl(120, 50%, 40%);
  --dark-green: hsl(120, 100%, 15%);
  --sage-green: hsl(152, 24%, 40%);
  --berry-red: hsl(343, 55%, 28%);
  --cream-beige: hsl(33, 20%, 20%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-serif;
  }
}

@layer components {
  .eco-gradient {
    background: linear-gradient(135deg, var(--gold-light) 0%, var(--gold-dark) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .hero-parallax {
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
  }

  .transition-all {
    transition: all 0.2s ease-in-out;
  }

  .product-card {
    @apply transition-all duration-300 hover:transform hover:-translate-y-2 hover:shadow-xl;
  }

  .pulse-heart {
    animation: pulse 2s infinite;
  }

  .cart-fly {
    animation: flyToCart 0.6s ease-out;
  }

  .fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }
}

@keyframes pulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
}

@keyframes flyToCart {
  0% { transform: scale(1) translateX(0); }
  50% { transform: scale(0.8) translateX(50px) translateY(-20px); }
  100% { transform: scale(0.6) translateX(100px) translateY(-40px); }
}

@keyframes fadeIn {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
}

::-webkit-scrollbar-thumb {
  background: var(--sage-green);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--forest-green);
}

