client/src/pages/product-detail.tsx.content:
import { useState } from "react";
import { useParams, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Star, Heart, ShoppingCart, Share2, Truck, Leaf, Shield, ArrowLeft, Camera } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion";
import { Separator } from "@/components/ui/separator";
import ProductCard from "@/components/product/product-card";
import ProductGallery from "@/components/product/product-gallery";
import ARTryOn from "@/components/ar/ar-try-on";
import { useCart } from "@/hooks/use-cart";
import { useWishlist } from "@/hooks/use-wishlist";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";

export default function ProductDetail() {
  const { id } = useParams();
  const [quantity, setQuantity] = useState(1);
  const [isAROpen, setIsAROpen] = useState(false);
  const { addToCart } = useCart();
  const { isInWishlist, addToWishlist, removeFromWishlist } = useWishlist();
  const { toast } = useToast();

  const { data: product, isLoading } = useQuery<Product>({
    queryKey: ["/api/products", id],
    enabled: !!id,
  });

  const { data: relatedProducts = [] } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    enabled: !!product?.category,
  });

  const isWishlisted = product ? isInWishlist(product.id) : false;

  const handleAddToCart = async () => {
    if (!product) return;

    try {
      await addToCart(product.id, quantity);
    } catch (error) {
      console.error("Failed to add to cart:", error);
    }
  };

  const handleWishlistToggle = async () => {
    if (!product) return;

    try {
      if (isWishlisted) {
        await removeFromWishlist(product.id);
      } else {
        await addToWishlist(product.id);
      }
    } catch (error) {
      console.error("Failed to update wishlist:", error);
    }
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: product?.name,
          text: product?.description,
          url: window.location.href,
        });
      } catch (error) {
        console.error("Failed to share:", error);
      }
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast({
        title: "Link copied",
        description: "Product link has been copied to clipboard",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20">
        <div className="container mx-auto px-4 py-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            <div className="bg-gray-200 aspect-square rounded-xl animate-pulse" />
            <div className="space-y-4">
              <div className="bg-gray-200 h-8 rounded animate-pulse" />
              <div className="bg-gray-200 h-6 rounded animate-pulse" />
              <div className="bg-gray-200 h-4 rounded animate-pulse" />
              <div className="bg-gray-200 h-32 rounded animate-pulse" />
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50 pt-20 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Product not found</h1>
          <Link href="/products">
            <Button>Back to Products</Button>
          </Link>
        </div>
      </div>
    );
  }

  const relatedProductsFiltered = relatedProducts
    .filter((p: Product) => p.id !== product.id)
    .slice(0, 4);

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="container mx-auto px-4 py-8">
        {/* Breadcrumb */}
        <div className="flex items-center gap-2 mb-8 text-sm text-gray-600">
          <Link href="/products" className="hover:text-[var(--forest-green)]">
            Products
          </Link>
          <span>/</span>
          <Link href={`/products/${product.category}`} className="hover:text-[var(--forest-green)]">
            {product.category}
          </Link>
          <span>/</span>
          <span className="text-gray-900">{product.name}</span>
        </div>

        {/* Back Button */}
        <Link href="/products">
          <Button variant="ghost" className="mb-6">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Products
          </Button>
        </Link>

        {/* Product Details */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 mb-16">
          {/* Product Gallery */}
          <div>
            <ProductGallery images={product.images || []} productName={product.name} />
          </div>

          {/* Product Info */}
          <div className="space-y-6">
            {/* Badges */}
            <div className="flex flex-wrap gap-2">
              {product.isVegan && (
                <Badge className="bg-[var(--sage-green)] text-white">Vegan</Badge>
              )}
              {product.isCrueltyFree && (
                <Badge className="bg-[var(--sage-green)] text-white">Cruelty-Free</Badge>
              )}
              {product.isOrganic && (
                <Badge className="bg-[var(--sage-green)] text-white">Organic</Badge>
              )}
              {product.trending && (
                <Badge className="bg-[var(--gold-light)] text-[var(--dark-green)]">Trending</Badge>
              )}
            </div>

            {/* Rating */}
            <div className="flex items-center gap-2">
              <div className="flex">
                {[1, 2, 3, 4, 5].map((star) => (
                  <Star
                    key={star}
                    className={`h-5 w-5 ${
                      star <= Math.floor(parseFloat(product.rating || '0'))
                        ? "text-yellow-400 fill-current"
                        : "text-gray-300"
                    }`}
                  />
                ))}
              </div>
              <span className="text-sm text-gray-600">
                {product.rating} ({product.reviewCount} reviews)
              </span>
            </div>

            {/* Title and Price */}
            <div>
              <h1 className="text-3xl font-serif font-bold text-[var(--dark-green)] mb-2">
                {product.name}
              </h1>
              <div className="text-4xl font-bold text-[var(--forest-green)] mb-4">
                ${product.price}
              </div>
            </div>

            {/* Description */}
            <p className="text-gray-700 leading-relaxed">{product.description}</p>

            {/* Key Benefits */}
            {product.benefits && product.benefits.length > 0 && (
              <div>
                <h3 className="font-semibold mb-2">Key Benefits:</h3>
                <ul className="list-disc list-inside text-gray-700 space-y-1">
                  {product.benefits.map((benefit) => (
                    <li key={benefit}>{benefit}</li>
                  ))}
                </ul>
              </div>
            )}

            {/* Quantity and Add to Cart */}
            <div className="space-y-4">
              <div className="flex items-center gap-4">
                <label className="font-semibold">Quantity:</label>
                <div className="flex items-center border rounded-lg">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    className="px-3"
                  >
                    -
                  </Button>
                  <span className="px-4 py-2 min-w-[3rem] text-center">{quantity}</span>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setQuantity(quantity + 1)}
                    className="px-3"
                  >
                    +
                  </Button>
                </div>
              </div>

              <div className="flex gap-4">
                <Button
                  size="lg"
                  onClick={handleAddToCart}
                  className="flex-1 bg-[var(--forest-green)] hover:bg-[var(--dark-green)]"
                >
                  <ShoppingCart className="h-5 w-5 mr-2" />
                  Add to Cart - ${(parseFloat(product.price) * quantity).toFixed(2)}
                </Button>

                <Button
                  variant="outline"
                  size="lg"
                  onClick={handleWishlistToggle}
                  className={`${isWishlisted ? "text-red-500 border-red-200" : ""}`}
                >
                  <Heart className={`h-5 w-5 ${isWishlisted ? "fill-current" : ""}`} />
                </Button>

                <Button variant="outline" size="lg" onClick={handleShare}>
                  <Share2 className="h-5 w-5" />
                </Button>
              </div>

              {/* AR Try-On for makeup products */}
              {product.category === "makeup" && (
                <Button
                  variant="outline"
                  size="lg"
                  onClick={() => setIsAROpen(true)}
                  className="w-full border-[var(--sage-green)] text-[var(--sage-green)] hover:bg-[var(--sage-green)] hover:text-white"
                >
                  <Camera className="h-5 w-5 mr-2" />
                  Try On with AR
                </Button>
              )}
            </div>

            {/* Features */}
            <div className="grid grid-cols-3 gap-4 pt-6 border-t">
              <div className="flex items-center gap-2 text-sm">
                <Leaf className="h-4 w-4 text-[var(--forest-green)]" />
                <span>Eco-Friendly</span>
              </div>
              <div className="flex items-center gap-2 text-sm">
                <Truck className="h-4 w-4 text-[var(--forest-green)]" />
                <span>Free Shipping</span>
              </div>
              <div className="flex items-center gap-2 text-sm">
                <Shield className="h-4 w-4 text-[var(--forest-green)]" />
                <span>30-Day Return</span>
              </div>
            </div>

            {/* Sustainability Score */}
            {product.sustainabilityScore && product.sustainabilityScore > 0 && (
              <div className="bg-[var(--cream-beige)] rounded-lg p-4">
                <div className="flex items-center gap-2 mb-2">
                  <Leaf className="h-5 w-5 text-[var(--forest-green)]" />
                  <span className="font-semibold">Sustainability Score</span>
                </div>
                <div className="flex items-center gap-4">
                  <div className="flex-1 bg-gray-200 rounded-full h-2">
                    <div
                      className="bg-[var(--forest-green)] h-2 rounded-full"
                      style={{ width: `${product.sustainabilityScore}%` }}
                    />
                  </div>
                  <span className="text-sm font-medium">{product.sustainabilityScore}%</span>
                </div>
                <p className="text-sm text-gray-600 mt-2">
                  Carbon footprint: {product.carbonFootprint}kg CO2e per product
                </p>
              </div>
            )}
          </div>
        </div>

        {/* Product Details Tabs */}
        <div className="mb-16">
          <Tabs defaultValue="details" className="w-full">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="details">Product Details</TabsTrigger>
              <TabsTrigger value="ingredients">Ingredients</TabsTrigger>
              <TabsTrigger value="reviews">Reviews</TabsTrigger>
            </TabsList>

            <TabsContent value="details" className="mt-6">
              <div className="bg-white rounded-lg p-6">
                <Accordion type="single" collapsible>
                  <AccordionItem value="description">
                    <AccordionTrigger>Full Description</AccordionTrigger>
                    <AccordionContent>
                      <p className="text-gray-700 leading-relaxed mb-4">
                        {product.description}
                      </p>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <h4 className="font-semibold mb-2">Suitable for:</h4>
                          <ul className="text-sm text-gray-600 space-y-1">
                            {product.skinTypes && product.skinTypes.map((type) => (
                              <li key={type}>{type} skin</li>
                            ))}
                          </ul>
                        </div>
                        <div>
                          <h4 className="font-semibold mb-2">Addresses:</h4>
                          <ul className="text-sm text-gray-600 space-y-1">
                            {product.concerns && product.concerns.map((concern) => (
                              <li key={concern}>{concern}</li>
                            ))}
                          </ul>
                        </div>
                      </div>
                    </AccordionContent>
                  </AccordionItem>

                  <AccordionItem value="usage">
                    <AccordionTrigger>How to Use</AccordionTrigger>
                    <AccordionContent>
                      <p className="text-gray-700">
                        Apply 2-3 drops to clean skin. Gently massage into face and neck. 
                        Use morning and evening for best results. Always follow with SPF during the day.
                      </p>
                    </AccordionContent>
                  </AccordionItem>

                  <AccordionItem value="sustainability">
                    <AccordionTrigger>Sustainability</AccordionTrigger>
                    <AccordionContent>
                      <div className="space-y-3">
                        <p className="text-gray-700">
                          This product is part of our commitment to sustainable beauty:
                        </p>
                        <ul className="list-disc list-inside text-gray-700 space-y-1">
                          <li>Packaging made from 100% recyclable materials</li>
                          <li>Carbon-neutral shipping</li>
                          <li>Ethically sourced ingredients</li>
                          <li>No harmful chemicals or parabens</li>
                        </ul>
                      </div>
                    </AccordionContent>
                  </AccordionItem>
                </Accordion>
              </div>
            </TabsContent>

            <TabsContent value="ingredients" className="mt-6">
              <div className="bg-white rounded-lg p-6">
                <h3 className="text-lg font-semibold mb-4">All Ingredients</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {product.ingredients && product.ingredients.map((ingredient) => (
                    <div key={ingredient} className="flex items-center gap-2 p-2 border rounded">
                      <Leaf className="h-4 w-4 text-[var(--forest-green)]" />
                      <span className="text-sm">{ingredient}</span>
                    </div>
                  ))}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="reviews" className="mt-6">
              <div className="bg-white rounded-lg p-6">
                <div className="text-center py-8">
                  <h3 className="text-lg font-semibold mb-2">Customer Reviews</h3>
                  <p className="text-gray-600 mb-4">
                    See what our customers are saying about this product
                  </p>
                  <div className="flex items-center justify-center gap-2 mb-4">
                    <div className="flex">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <Star
                          key={star}
                          className="h-6 w-6 text-yellow-400 fill-current"
                        />
                      ))}
                    </div>
                    <span className="text-lg font-semibold">{product.rating}</span>
                    <span className="text-gray-600">({product.reviewCount} reviews)</span>
                  </div>
                  <Button variant="outline">Write a Review</Button>
                </div>
              </div>
            </TabsContent>
          </Tabs>
        </div>

        {/* Related Products */}
        {relatedProductsFiltered.length > 0 && (
          <div>
            <h2 className="text-3xl font-serif font-bold text-[var(--dark-green)] mb-8">
              You Might Also Like
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {relatedProductsFiltered.map((relatedProduct: Product) => (
                <ProductCard key={relatedProduct.id} product={relatedProduct} />
              ))}
            </div>
          </div>
        )}
      </div>

      {/* AR Try-On Modal */}
      <ARTryOn 
        isOpen={isAROpen} 
        onClose={() => setIsAROpen(false)} 
        product={product}
      />
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState } from "react";
import { useParams } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Search, Filter, Grid, List, SlidersHorizontal } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import ProductCard from "@/components/product/product-card";
import { PRODUCT_CATEGORIES, SKIN_TYPES, SKIN_CONCERNS } from "@/lib/constants";
import type { Product } from "@shared/schema";

export default function Products() {
  const { category } = useParams();
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("featured");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [filters, setFilters] = useState({
    skinTypes: [] as string[],
    concerns: [] as string[],
    features: [] as string[],
    priceRange: "",
  });

  const { data: allProducts = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: searchResults = [] } = useQuery<Product[]>({
    queryKey: ["/api/products/search", searchQuery],
    enabled: searchQuery.length > 2,
  });

  const products = searchQuery.length > 2 ? searchResults : allProducts;

  const filteredProducts = products.filter((product: Product) => {
    // Category filter
    if (category && product.category !== category) return false;

    // Skin type filter
    if (filters.skinTypes.length > 0) {
      const hasMatchingSkinType = filters.skinTypes.some(type => 
        product.skinTypes && product.skinTypes.includes(type)
      );
      if (!hasMatchingSkinType) return false;
    }

    // Concerns filter
    if (filters.concerns.length > 0) {
      const hasMatchingConcern = filters.concerns.some(concern => 
        product.concerns && product.concerns.includes(concern)
      );
      if (!hasMatchingConcern) return false;
    }

    // Features filter
    if (filters.features.length > 0) {
      if (filters.features.includes("vegan") && !product.isVegan) return false;
      if (filters.features.includes("cruelty-free") && !product.isCrueltyFree) return false;
      if (filters.features.includes("organic") && !product.isOrganic) return false;
    }

    // Price range filter
    if (filters.priceRange) {
      const price = parseFloat(product.price);
      switch (filters.priceRange) {
        case "under-25":
          if (price >= 25) return false;
          break;
        case "25-50":
          if (price < 25 || price >= 50) return false;
          break;
        case "50-100":
          if (price < 50 || price >= 100) return false;
          break;
        case "over-100":
          if (price < 100) return false;
          break;
      }
    }

    return true;
  });

  const sortedProducts = [...filteredProducts].sort((a: Product, b: Product) => {
    switch (sortBy) {
      case "price-low":
        return parseFloat(a.price) - parseFloat(b.price);
      case "price-high":
        return parseFloat(b.price) - parseFloat(a.price);
      case "rating":
        return parseFloat(b.rating || '0') - parseFloat(a.rating || '0');
      case "newest":
        return (b.createdAt ? new Date(b.createdAt).getTime() : 0) - (a.createdAt ? new Date(a.createdAt).getTime() : 0);
      case "featured":
      default:
        return (b.featured ? 1 : 0) - (a.featured ? 1 : 0);
    }
  });

  const handleFilterChange = (filterType: keyof typeof filters, value: string | string[]) => {
    setFilters(prev => ({ ...prev, [filterType]: value }));
  };

  const clearFilters = () => {
    setFilters({
      skinTypes: [],
      concerns: [],
      features: [],
      priceRange: "",
    });
  };

  const FilterContent = () => (
    <div className="space-y-6">
      {/* Skin Types */}
      <div>
        <h3 className="font-semibold mb-3">Skin Type</h3>
        <div className="space-y-2">
          {Object.entries(SKIN_TYPES).map(([value, label]) => (
            <Label key={value} className="flex items-center space-x-2">
              <Checkbox
                checked={filters.skinTypes.includes(value)}
                onCheckedChange={(checked) => {
                  if (checked) {
                    handleFilterChange("skinTypes", [...filters.skinTypes, value]);
                  } else {
                    handleFilterChange("skinTypes", filters.skinTypes.filter(t => t !== value));
                  }
                }}
              />
              <span>{label}</span>
            </Label>
          ))}
        </div>
      </div>

      <Separator />

      {/* Skin Concerns */}
      <div>
        <h3 className="font-semibold mb-3">Skin Concerns</h3>
        <div className="space-y-2">
          {Object.entries(SKIN_CONCERNS).map(([value, label]) => (
            <Label key={value} className="flex items-center space-x-2">
              <Checkbox
                checked={filters.concerns.includes(value)}
                onCheckedChange={(checked) => {
                  if (checked) {
                    handleFilterChange("concerns", [...filters.concerns, value]);
                  } else {
                    handleFilterChange("concerns", filters.concerns.filter(c => c !== value));
                  }
                }}
              />
              <span>{label}</span>
            </Label>
          ))}
        </div>
      </div>

      <Separator />

      {/* Features */}
      <div>
        <h3 className="font-semibold mb-3">Features</h3>
        <div className="space-y-2">
          {[
            { value: "vegan", label: "Vegan" },
            { value: "cruelty-free", label: "Cruelty-Free" },
            { value: "organic", label: "Organic" },
          ].map((feature) => (
            <Label key={feature.value} className="flex items-center space-x-2">
              <Checkbox
                checked={filters.features.includes(feature.value)}
                onCheckedChange={(checked) => {
                  if (checked) {
                    handleFilterChange("features", [...filters.features, feature.value]);
                  } else {
                    handleFilterChange("features", filters.features.filter(f => f !== feature.value));
                  }
                }}
              />
              <span>{feature.label}</span>
            </Label>
          ))}
        </div>
      </div>

      <Separator />

      {/* Price Range */}
      <div>
        <h3 className="font-semibold mb-3">Price Range</h3>
        <Select value={filters.priceRange} onValueChange={(value) => handleFilterChange("priceRange", value)}>
          <SelectTrigger>
            <SelectValue placeholder="Select price range" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="under-25">Under $25</SelectItem>
            <SelectItem value="25-50">$25 - $50</SelectItem>
            <SelectItem value="50-100">$50 - $100</SelectItem>
            <SelectItem value="over-100">Over $100</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <Button onClick={clearFilters} variant="outline" className="w-full">
        Clear All Filters
      </Button>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 pt-20">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-serif font-bold text-[var(--dark-green)] mb-4">
            {category ? PRODUCT_CATEGORIES[category as keyof typeof PRODUCT_CATEGORIES] : "All Products"}
          </h1>
          <p className="text-gray-600">
            Discover our sustainable beauty collection featuring plant-powered formulations and eco-friendly packaging
          </p>
        </div>

        {/* Search and Filters Bar */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <div className="flex flex-col md:flex-row gap-4 items-center">
            {/* Search */}
            <div className="relative flex-1">
              <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
              <Input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
            </div>

            {/* Sort */}
            <Select value={sortBy} onValueChange={setSortBy}>
              <SelectTrigger className="w-48">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="featured">Featured</SelectItem>
                <SelectItem value="price-low">Price: Low to High</SelectItem>
                <SelectItem value="price-high">Price: High to Low</SelectItem>
                <SelectItem value="rating">Highest Rated</SelectItem>
                <SelectItem value="newest">Newest</SelectItem>
              </SelectContent>
            </Select>

            {/* View Mode */}
            <div className="flex border rounded-lg">
              <Button
                variant={viewMode === "grid" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("grid")}
                className="rounded-r-none"
              >
                <Grid className="h-4 w-4" />
              </Button>
              <Button
                variant={viewMode === "list" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("list")}
                className="rounded-l-none"
              >
                <List className="h-4 w-4" />
              </Button>
            </div>

            {/* Mobile Filter Button */}
            <Sheet>
              <SheetTrigger asChild>
                <Button variant="outline" className="md:hidden">
                  <SlidersHorizontal className="h-4 w-4 mr-2" />
                  Filters
                </Button>
              </SheetTrigger>
              <SheetContent side="right">
                <SheetHeader>
                  <SheetTitle>Filters</SheetTitle>
                </SheetHeader>
                <div className="mt-6">
                  <FilterContent />
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>

        <div className="flex gap-8">
          {/* Desktop Filters Sidebar */}
          <div className="hidden md:block w-64 flex-shrink-0">
            <div className="bg-white rounded-lg shadow-sm p-6 sticky top-24">
              <div className="flex items-center gap-2 mb-4">
                <Filter className="h-5 w-5" />
                <h2 className="text-lg font-semibold">Filters</h2>
              </div>
              <FilterContent />
            </div>
          </div>

          {/* Products Grid */}
          <div className="flex-1">
            {isLoading ? (
              <div className={`grid gap-6 ${
                viewMode === "grid" 
                  ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3" 
                  : "grid-cols-1"
              }`}>
                {[1, 2, 3, 4, 5, 6].map((i) => (
                  <div key={i} className="bg-gray-200 rounded-xl h-96 animate-pulse" />
                ))}
              </div>
            ) : sortedProducts.length > 0 ? (
              <>
                <div className="mb-6 text-sm text-gray-600">
                  Showing {sortedProducts.length} of {products.length} products
                </div>
                <div className={`grid gap-6 ${
                  viewMode === "grid" 
                    ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3" 
                    : "grid-cols-1"
                }`}>
                  {sortedProducts.map((product: Product) => (
                    <ProductCard 
                      key={product.id} 
                      product={product}
                      variant={viewMode}
                    />
                  ))}
                </div>
              </>
            ) : (
              <div className="text-center py-12">
                <div className="w-24 h-24 bg-gray-100 rounded-full mx-auto mb-4 flex items-center justify-center">
                  <Search className="h-12 w-12 text-gray-400" />
                </div>
                <h3 className="text-xl font-semibold mb-2">No products found</h3>
                <p className="text-gray-600 mb-4">
                  Try adjusting your filters or search terms to find what you're looking for.
                </p>
                <Button onClick={clearFilters} variant="outline">
                  Clear Filters
                </Button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ar/ar-try-on.tsx.content:
import { useState, useRef, useEffect } from "react";
import { X, Camera, RotateCcw, Download, Share2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Slider } from "@/components/ui/slider";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import type { Product } from "@shared/schema";

interface ARTryOnProps {
  isOpen: boolean;
  onClose: () => void;
  product: Product;
}

export default function ARTryOn({ isOpen, onClose, product }: ARTryOnProps) {
  const [hasPermission, setHasPermission] = useState<boolean | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedShade, setSelectedShade] = useState(0);
  const [intensity, setIntensity] = useState([70]);
  const [capturedImage, setCapturedImage] = useState<string | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const { toast } = useToast();

  // Mock shades for the product
  const productShades = [
    { name: "Natural Rose", color: "#E8A598" },
    { name: "Berry Blush", color: "#D67B7B" },
    { name: "Coral Sunset", color: "#FF7F7F" },
    { name: "Deep Berry", color: "#A0546A" },
    { name: "Classic Red", color: "#DC143C" },
  ];

  useEffect(() => {
    if (isOpen && hasPermission === null) {
      requestCameraPermission();
    }

    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
        streamRef.current = null;
      }
    };
  }, [isOpen]);

  const requestCameraPermission = async () => {
    setIsLoading(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        } 
      });

      streamRef.current = stream;
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
      setHasPermission(true);

      toast({
        title: "Camera access granted",
        description: "You can now try on the product virtually!",
      });
    } catch (error) {
      setHasPermission(false);
      toast({
        title: "Camera access denied",
        description: "Please allow camera access to use the AR try-on feature.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const context = canvas.getContext('2d');

    if (context) {
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;

      // Draw the video frame
      context.drawImage(video, 0, 0);

      // Simulate AR overlay (in a real implementation, this would use face detection)
      const selectedColor = productShades[selectedShade].color;
      context.fillStyle = selectedColor;
      context.globalAlpha = intensity[0] / 100;

      // Mock lip area (in real AR, this would be detected via face landmarks)
      const centerX = canvas.width / 2;
      const centerY = canvas.height * 0.65;
      const lipWidth = 60;
      const lipHeight = 20;

      context.beginPath();
      context.ellipse(centerX, centerY, lipWidth, lipHeight, 0, 0, 2 * Math.PI);
      context.fill();

      context.globalAlpha = 1;

      const imageData = canvas.toDataURL('image/png');
      setCapturedImage(imageData);

      toast({
        title: "Photo captured!",
        description: "Your AR try-on photo has been saved.",
      });
    }
  };

  const downloadImage = () => {
    if (!capturedImage) return;

    const link = document.createElement('a');
    link.download = `ecoglow-ar-tryon-${product.name.toLowerCase().replace(/\s+/g, '-')}.png`;
    link.href = capturedImage;
    link.click();
  };

  const shareImage = async () => {
    if (!capturedImage) return;

    try {
      // Convert data URL to blob
      const response = await fetch(capturedImage);
      const blob = await response.blob();
      const file = new File([blob], 'ar-tryon.png', { type: 'image/png' });

      if (navigator.share && navigator.canShare({ files: [file] })) {
        await navigator.share({
          title: `AR Try-On: ${product.name}`,
          text: `Check out how I look with ${product.name} from EcoGlow!`,
          files: [file],
        });
      } else {
        // Fallback: copy image to clipboard
        await navigator.clipboard.write([
          new ClipboardItem({ 'image/png': blob })
        ]);
        toast({
          title: "Image copied",
          description: "AR try-on image copied to clipboard!",
        });
      }
    } catch (error) {
      toast({
        title: "Share failed",
        description: "Unable to share the image. Please try downloading instead.",
        variant: "destructive",
      });
    }
  };

  const resetTryOn = () => {
    setCapturedImage(null);
    setSelectedShade(0);
    setIntensity([70]);
  };

  const handleClose = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    setCapturedImage(null);
    setHasPermission(null);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="text-2xl font-serif text-[var(--dark-green)]">
              AR Try-On: {product.name}
            </DialogTitle>
            <Button variant="ghost" size="sm" onClick={handleClose}>
              <X className="h-4 w-4" />
            </Button>
          </div>
          <DialogDescription className="sr-only">
            Try on {product.name} virtually using your camera with adjustable shade and intensity controls
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Camera Permission / Loading State */}
          {hasPermission === null || isLoading ? (
            <div className="text-center py-12">
              <div className="w-16 h-16 border-4 border-[var(--sage-green)] border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
              <h3 className="text-lg font-semibold mb-2">
                {isLoading ? "Accessing camera..." : "Setting up AR..."}
              </h3>
              <p className="text-gray-600">
                Please allow camera access to use the virtual try-on feature
              </p>
            </div>
          ) : hasPermission === false ? (
            <div className="text-center py-12">
              <Camera className="h-16 w-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">Camera Access Required</h3>
              <p className="text-gray-600 mb-6">
                To use the AR try-on feature, we need access to your camera. 
                Your privacy is protected - video is not recorded or stored.
              </p>
              <Button 
                onClick={requestCameraPermission}
                className="bg-[var(--forest-green)] hover:bg-[var(--dark-green)]"
              >
                <Camera className="h-4 w-4 mr-2" />
                Enable Camera
              </Button>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* Camera/Preview Area */}
              <div className="lg:col-span-2">
                <Card>
                  <CardContent className="p-4">
                    <div className="relative aspect-video bg-gray-100 rounded-lg overflow-hidden">
                      {capturedImage ? (
                        <img
                          src={capturedImage}
                          alt="AR Try-on capture"
                          className="w-full h-full object-cover"
                        />
                      ) : (
                        <>
                          <video
                            ref={videoRef}
                            autoPlay
                            muted
                            playsInline
                            className="w-full h-full object-cover scale-x-[-1]"
                          />
                          <canvas ref={canvasRef} className="hidden" />

                          {/* AR Overlay Instructions */}
                          <div className="absolute top-4 left-4 bg-black/50 text-white px-3 py-2 rounded-lg text-sm">
                            Position your face in the center
                          </div>

                          {/* Live AR Preview */}
                          <div 
                            className="absolute inset-0 pointer-events-none"
                            style={{
                              background: `radial-gradient(ellipse 60px 20px at center 65%, ${productShades[selectedShade].color}${Math.floor(intensity[0] * 2.55).toString(16).padStart(2, '0')} 0%, transparent 100%)`
                            }}
                          />
                        </>
                      )}
                    </div>

                    {/* Camera Controls */}
                    <div className="flex justify-center gap-2 mt-4">
                      {capturedImage ? (
                        <>
                          <Button onClick={resetTryOn} variant="outline">
                            <RotateCcw className="h-4 w-4 mr-2" />
                            Try Again
                          </Button>
                          <Button onClick={downloadImage} variant="outline">
                            <Download className="h-4 w-4 mr-2" />
                            Download
                          </Button>
                          <Button onClick={shareImage} className="bg-[var(--forest-green)]">
                            <Share2 className="h-4 w-4 mr-2" />
                            Share
                          </Button>
                        </>
                      ) : (
                        <Button 
                          onClick={capturePhoto}
                          size="lg"
                          className="bg-[var(--berry-red)] hover:bg-red-700"
                        >
                          <Camera className="h-5 w-5 mr-2" />
                          Capture Photo
                        </Button>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Controls Panel */}
              <div className="space-y-6">
                <Card>
                  <CardContent className="p-4">
                    <h3 className="font-semibold mb-4">Choose Your Shade</h3>
                    <div className="grid grid-cols-1 gap-2">
                      {productShades.map((shade, index) => (
                        <button
                          key={shade.name}
                          onClick={() => setSelectedShade(index)}
                          className={`flex items-center gap-3 p-3 rounded-lg border transition-all ${
                            selectedShade === index 
                              ? "border-[var(--forest-green)] bg-[var(--sage-green)]/10" 
                              : "border-gray-200 hover:border-gray-300"
                          }`}
                        >
                          <div
                            className="w-6 h-6 rounded-full border-2 border-white shadow-sm"
                            style={{ backgroundColor: shade.color }}
                          />
                          <span className="text-sm font-medium">{shade.name}</span>
                        </button>
                      ))}
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-4">
                    <div className="space-y-4">
                      <Label>Intensity: {intensity[0]}%</Label>
                      <Slider
                        value={intensity}
                        onValueChange={setIntensity}
                        max={100}
                        min={20}
                        step={5}
                        className="w-full"
                      />
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-4">
                    <h3 className="font-semibold mb-2">Product Info</h3>
                    <div className="space-y-2 text-sm">
                      <div>
                        <span className="text-gray-600">Name:</span> {product.name}
                      </div>
                      <div>
                        <span className="text-gray-600">Price:</span> ${product.price}
                      </div>
                      <div>
                        <span className="text-gray-600">Rating:</span> {product.rating}/5
                      </div>
                      {product.isVegan && (
                        <div className="flex items-center gap-1">
                          <span className="w-2 h-2 bg-[var(--forest-green)] rounded-full"></span>
                          <span className="text-[var(--forest-green)]">Vegan</span>
                        </div>
                      )}
                      {product.isCrueltyFree && (
                        <div className="flex items-center gap-1">
                          <span className="w-2 h-2 bg-[var(--forest-green)] rounded-full"></span>
                          <span className="text-[var(--forest-green)]">Cruelty-Free</span>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>

                <div className="text-center text-xs text-gray-500 bg-gray-50 p-3 rounded-lg">
                  <span>ðŸ”’ Your privacy is protected</span>
                  <br />
                  Video is processed locally and not stored
                </div>
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

