import { useState } from "react";
import { Link, useLocation } from "wouter";
import { Search, Heart, ShoppingBag, User, Menu, Mic, Leaf } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { Badge } from "@/components/ui/badge";
import { useCart } from "@/hooks/use-cart";
import { useVoiceSearch } from "@/hooks/use-voice-search";
import CartSidebar from "@/components/cart/cart-sidebar";
import VoiceSearch from "@/components/search/voice-search";

export default function Header() {
  const [location] = useLocation();
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [isSearchExpanded, setIsSearchExpanded] = useState(false);
  const { itemCount } = useCart();
  const { isListening, startListening } = useVoiceSearch();

  const navigation = [
    { name: "Products", href: "/products" },
    { name: "Quiz", href: "/quiz" },
    { name: "Sustainability", href: "/sustainability" },
    { name: "Blog", href: "/blog" },
    { name: "Account", href: "/account" },
  ];

  const isActive = (href: string) => {
    return location === href || (href !== "/" && location.startsWith(href));
  };

  const handleVoiceSearch = () => {
    startListening();
  };

  return (
    <>
      <header className="fixed top-0 w-full bg-white/95 backdrop-blur-sm shadow-md z-50 border-b">
        <div className="container mx-auto px-4 py-3">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <Link href="/" className="flex items-center space-x-2 group">
              <Leaf className="h-8 w-8 text-[var(--forest-green)] group-hover:text-[var(--dark-green)] transition-colors" />
              <span className="text-2xl font-serif font-bold text-[var(--dark-green)]">
                EcoGlow
              </span>
            </Link>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex space-x-8">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  href={item.href}
                  className={`font-medium transition-colors hover:text-[var(--forest-green)] ${
                    isActive(item.href)
                      ? "text-[var(--forest-green)]"
                      : "text-gray-700"
                  }`}
                >
                  {item.name}
                </Link>
              ))}
            </nav>

            {/* Search & Icons */}
            <div className="flex items-center space-x-4">
              {/* Search Bar */}
              <div className="relative hidden md:block">
                <div className={`flex items-center transition-all duration-300 ${
                  isSearchExpanded ? "w-80" : "w-64"
                }`}>
                  <Input
                    type="text"
                    placeholder="Search sustainable beauty..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onFocus={() => setIsSearchExpanded(true)}
                    onBlur={() => setIsSearchExpanded(false)}
                    className="pr-20 rounded-full border-[var(--sage-green)] focus:ring-[var(--forest-green)]"
                  />
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleVoiceSearch}
                    className={`absolute right-10 h-8 w-8 p-0 rounded-full ${
                      isListening ? "text-red-500 animate-pulse" : "text-[var(--sage-green)]"
                    }`}
                  >
                    <Mic className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="absolute right-2 h-8 w-8 p-0 rounded-full text-[var(--sage-green)]"
                  >
                    <Search className="h-4 w-4" />
                  </Button>
                </div>
              </div>

              {/* Icons */}
              <Link href="/account">
                <Button variant="ghost" size="sm" className="p-2">
                  <Heart className="h-5 w-5 text-[var(--sage-green)]" />
                </Button>
              </Link>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsCartOpen(true)}
                className="relative p-2"
              >
                <ShoppingBag className="h-5 w-5 text-[var(--sage-green)]" />
                {itemCount > 0 && (
                  <Badge
                    variant="destructive"
                    className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center text-xs bg-[var(--berry-red)]"
                  >
                    {itemCount}
                  </Badge>
                )}
              </Button>

              <Link href="/account">
                <Button variant="ghost" size="sm" className="p-2">
                  <User className="h-5 w-5 text-[var(--sage-green)]" />
                </Button>
              </Link>

              {/* Mobile Menu */}
              <Sheet>
                <SheetTrigger asChild>
                  <Button variant="ghost" size="sm" className="md:hidden p-2">
                    <Menu className="h-5 w-5 text-[var(--sage-green)]" />
                  </Button>
                </SheetTrigger>
                <SheetContent side="right" className="w-64">
                  <div className="flex flex-col space-y-4 mt-8">
                    <div className="relative">
                      <Input
                        type="text"
                        placeholder="Search..."
                        className="pr-10"
                      />
                      <Search className="absolute right-3 top-3 h-4 w-4 text-gray-400" />
                    </div>

                    {navigation.map((item) => (
                      <Link
                        key={item.name}
                        href={item.href}
                        className={`block py-2 px-4 rounded-lg transition-colors ${
                          isActive(item.href)
                            ? "bg-[var(--cream-beige)] text-[var(--forest-green)]"
                            : "text-gray-700 hover:bg-gray-100"
                        }`}
                      >
                        {item.name}
                      </Link>
                    ))}
                  </div>
                </SheetContent>
              </Sheet>
            </div>
          </div>
        </div>
      </header>

      {/* Cart Sidebar */}
      <CartSidebar isOpen={isCartOpen} onClose={() => setIsCartOpen(false)} />

      {/* Voice Search Component */}
      <VoiceSearch searchQuery={searchQuery} onSearchChange={setSearchQuery} />
    </>
  );
}

client/src/components/product/product-ca.content:
import { useState } from "react";
import { Link } from "wouter";
import { Heart, ShoppingCart, Star, Leaf } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { useCart } from "@/hooks/use-cart";
import { useWishlist } from "@/hooks/use-wishlist";
import type { Product } from "@shared/schema";

interface ProductCardProps {
  product: Product;
  className?: string;
  variant?: "grid" | "list";
}

export default function ProductCard({ product, className = "", variant = "grid" }: ProductCardProps) {
  const [isImageLoaded, setIsImageLoaded] = useState(false);
  const { addToCart } = useCart();
  const { isInWishlist, addToWishlist, removeFromWishlist } = useWishlist();
  const isWishlisted = isInWishlist(product.id);

  const handleAddToCart = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      await addToCart(product.id);
    } catch (error) {
      console.error("Failed to add to cart:", error);
    }
  };

  const handleWishlistToggle = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    try {
      if (isWishlisted) {
        await removeFromWishlist(product.id);
      } else {
        await addToWishlist(product.id);
      }
    } catch (error) {
      console.error("Failed to update wishlist:", error);
    }
  };

  const renderSustainabilityBadges = () => {
    const badges = [];
    if (product.isVegan) badges.push("Vegan");
    if (product.isCrueltyFree) badges.push("Cruelty-Free");
    if (product.isOrganic) badges.push("Organic");
    if (product.trending) badges.push("Trending");
    if (product.featured) badges.push("Featured");

    return badges.slice(0, 2).map((badge) => (
      <Badge
        key={badge}
        variant="secondary"
        className={`text-xs ${
          badge === "Trending" ? "bg-[var(--gold-light)] text-[var(--dark-green)]" :
          badge === "Featured" ? "bg-[var(--light-green)] text-[var(--dark-green)]" :
          "bg-[var(--sage-green)] text-white"
        }`}
      >
        {badge}
      </Badge>
    ));
  };

  const isListView = variant === "list";

  return (
    <Card className={`product-card group cursor-pointer overflow-hidden bg-white shadow-lg ${isListView ? 'flex' : ''} ${className}`}>
      <Link href={`/product/${product.id}`} className={isListView ? 'flex w-full' : ''}>
        <div className={`relative ${isListView ? 'w-48 flex-shrink-0' : ''}`}>
          <div className={`overflow-hidden ${isListView ? 'aspect-[4/3]' : 'aspect-square'}`}>
            <img
              src={product.images && product.images[0] || "/placeholder-product.jpg"}
              alt={product.name}
              onLoad={() => setIsImageLoaded(true)}
              className={`w-full h-full object-cover group-hover:scale-105 transition-transform duration-300 ${
                isImageLoaded ? "opacity-100" : "opacity-0"
              }`}
            />
            {!isImageLoaded && (
              <div className="absolute inset-0 bg-gray-200 animate-pulse" />
            )}
          </div>

          {/* Overlay Badges */}
          <div className="absolute top-3 left-3 flex flex-col gap-1">
            {renderSustainabilityBadges()}
          </div>

          {/* Wishlist Button */}
          <Button
            variant="ghost"
            size="sm"
            onClick={handleWishlistToggle}
            className={`absolute top-3 right-3 h-8 w-8 p-0 rounded-full bg-white/80 backdrop-blur-sm hover:bg-white ${
              isWishlisted ? "text-red-500" : "text-gray-600"
            }`}
          >
            <Heart className={`h-4 w-4 ${isWishlisted ? "fill-current" : ""}`} />
          </Button>

          {/* Sustainability Score */}
          {product.sustainabilityScore && product.sustainabilityScore > 80 && (
            <div className="absolute bottom-3 left-3 flex items-center gap-1 bg-[var(--forest-green)]/90 text-white px-2 py-1 rounded-full text-xs">
              <Leaf className="h-3 w-3" />
              <span>{product.sustainabilityScore}% Eco</span>
            </div>
          )}
        </div>

        <CardContent className={`${isListView ? 'flex-1 p-6 flex flex-col justify-between' : 'p-6'}`}>
          <div className={isListView ? 'space-y-3' : ''}>
            <div className="flex items-center gap-1 mb-2">
              <div className="flex">
                {[1, 2, 3, 4, 5].map((star) => (
                  <Star
                    key={star}
                    className={`h-4 w-4 ${
                      star <= Math.floor(parseFloat(product.rating || '0'))
                        ? "text-yellow-400 fill-current"
                        : "text-gray-300"
                    }`}
                  />
                ))}
              </div>
              <span className="text-sm text-gray-600">({product.reviewCount})</span>
            </div>

            <h3 className={`font-semibold mb-2 group-hover:text-[var(--forest-green)] transition-colors ${
              isListView ? 'text-xl' : 'text-lg'
            }`}>
              {product.name}
            </h3>

            <p className={`text-gray-600 text-sm mb-4 ${isListView ? 'line-clamp-3' : 'line-clamp-2'}`}>
              {product.description}
            </p>

            <div className={`flex items-center ${isListView ? 'justify-between' : 'justify-between'}`}>
              <div className="flex flex-col">
                <span className={`font-bold text-[var(--forest-green)] ${isListView ? 'text-3xl' : 'text-2xl'}`}>
                  ${product.price}
                </span>
                {product.stock && product.stock < 10 && product.stock > 0 && (
                  <span className="text-xs text-orange-500">
                    Only {product.stock} left
                  </span>
                )}
              </div>

              <Button
                onClick={handleAddToCart}
                className={`bg-[var(--forest-green)] hover:bg-[var(--dark-green)] text-white ${
                  isListView ? 'px-6 py-2' : ''
                }`}
              >
                <ShoppingCart className="h-4 w-4 mr-2" />
                Add to Cart
              </Button>
            </div>

            {/* Key Benefits */}
            {product.benefits && product.benefits.length > 0 && (
              <div className={`pt-4 border-t ${isListView ? 'mt-4' : 'mt-4'}`}>
                <div className="flex flex-wrap gap-1">
                  {product.benefits.slice(0, isListView ? 4 : 3).map((benefit) => (
                    <Badge
                      key={benefit}
                      variant="outline"
                      className="text-xs text-[var(--forest-green)] border-[var(--sage-green)]"
                    >
                      {benefit}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Link>
    </Card>
  );
}

client/src/components/product/product-ga.content:
import { useState } from "react";
import { ChevronLeft, ChevronRight, ZoomIn } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTrigger } from "@/components/ui/dialog";

interface ProductGalleryProps {
  images: string[];
  productName: string;
}

export default function ProductGallery({ images, productName }: ProductGalleryProps) {
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [isZoomed, setIsZoomed] = useState(false);

  const handlePrevious = () => {
    setSelectedImageIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setSelectedImageIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
  };

  if (!images || images.length === 0) {
    return (
      <div className="aspect-square bg-gray-200 rounded-xl flex items-center justify-center">
        <span className="text-gray-400">No image available</span>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Main Image */}
      <div className="relative group">
        <div className="aspect-square overflow-hidden rounded-xl bg-gray-100">
          <img
            src={images[selectedImageIndex]}
            alt={`${productName} - View ${selectedImageIndex + 1}`}
            className="w-full h-full object-cover"
          />
        </div>

        {/* Navigation Arrows */}
        {images.length > 1 && (
          <>
            <Button
              variant="ghost"
              size="sm"
              onClick={handlePrevious}
              className="absolute left-2 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm hover:bg-white opacity-0 group-hover:opacity-100 transition-opacity"
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleNext}
              className="absolute right-2 top-1/2 -translate-y-1/2 bg-white/80 backdrop-blur-sm hover:bg-white opacity-0 group-hover:opacity-100 transition-opacity"
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </>
        )}

        {/* Zoom Button */}
        <Dialog>
          <DialogTrigger asChild>
            <Button
              variant="ghost"
              size="sm"
              className="absolute top-2 right-2 bg-white/80 backdrop-blur-sm hover:bg-white opacity-0 group-hover:opacity-100 transition-opacity"
            >
              <ZoomIn className="h-4 w-4" />
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-4xl">
            <img
              src={images[selectedImageIndex]}
              alt={`${productName} - Zoomed view`}
              className="w-full h-auto max-h-[80vh] object-contain"
            />
          </DialogContent>
        </Dialog>
      </div>

      {/* Thumbnail Grid */}
      {images.length > 1 && (
        <div className="grid grid-cols-4 gap-2">
          {images.map((image, index) => (
            <button
              key={index}
              onClick={() => setSelectedImageIndex(index)}
              className={`aspect-square overflow-hidden rounded-lg border-2 transition-all ${
                index === selectedImageIndex
                  ? "border-[var(--forest-green)] opacity-100"
                  : "border-gray-200 opacity-60 hover:opacity-80"
              }`}
            >
              <img
                src={image}
                alt={`${productName} - Thumbnail ${index + 1}`}
                className="w-full h-full object-cover"
              />
            </button>
          ))}
        </div>
      )}

      {/* Image Indicators */}
      {images.length > 1 && (
        <div className="flex justify-center space-x-2">
          {images.map((_, index) => (
            <button
              key={index}
              onClick={() => setSelectedImageIndex(index)}
              className={`w-2 h-2 rounded-full transition-all ${
                index === selectedImageIndex
                  ? "bg-[var(--forest-green)]"
                  : "bg-gray-300 hover:bg-gray-400"
              }`}
            />
          ))}
        </div>
      )}
    </div>
  );
}

